import Terminal from "./Terminal";
import {Box} from "@mui/material";
import {info} from "../../info/Info";


export default function About({innerRef}) {
    const firstName = info.firstName.toLowerCase()

function aboutMeText() {
    return (
        <>
            <p style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                üåü Hello! I‚Äôm a Research Scientist at Meta (GenAI org.), where I dive into the fascinating world of diffusion models and large-scale training. These models enable the generation of 3D assets and media editing capabilities. üé®‚ú® Before, I had interned at Microsoft Research Lab and Huawei Noah's Ark.
            </p>
            <p style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                üöÄ <strong>Collaborations:</strong> I‚Äôm fortunate to work alongside ‚ÄîAndrea Vedaldi and Natalia Neverova and the rest of the 3D team. Together, we‚Äôre pushing the boundaries of what‚Äôs possible in computer vision and graphics.
            </p>
            <p style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                üéì <strong>My Mentors:</strong> During my academic journey, I‚Äôve been mentored by some incredible professors:
            </p>
            <ul style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                <li>Iasonas Kokkinos (UCL)</li>
                <li>Stamatis Lefkimmiatis (SkolTech)</li>
                <li>Alexandros Potamianos (NTUA)</li>
            </ul>
            <p style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                üë©‚Äçüéì <strong>Supervising Students:</strong> As a mentor, I‚Äôve had the honor of guiding brilliant students:
            </p>
            <ul style={{ color: "#1f1f1f", fontFamily: "RobotoRegular, sans-serif", fontSize: "17px" }}>
                <li>Junlin Han: Ph.D with Oxford</li>
                <li>Shalini Maiti: Ph.D with UCL</li>
                <li>Eric Tuan Le</li>
            </ul>
        </>
    );
}


    return (
        <Box ref={innerRef} display={'flex'} flexDirection={'column'} alignItems={'center'} mt={'3rem'} id={'about'}>
            <Terminal text={aboutMeText()}/>
        </Box>
    )
}
